require('@babel/register');
const gulp = require('gulp');
const postcss = require('gulp-postcss');
const tailwindcss = require('tailwindcss');
const imagemin = require('gulp-imagemin');
const browserSync = require('browser-sync').create();
const log = require('fancy-log');

gulp.task('styles', function() {
    return gulp.src('src/styles.css')
        .pipe(postcss([tailwindcss]))
        .pipe(gulp.dest('dist'))
        .pipe(browserSync.stream());
});

gulp.task('html', function() {
    return gulp.src('src/*.html')
        .pipe(gulp.dest('dist'))
        .pipe(browserSync.stream());
});

gulp.task('scripts', function() {
    return gulp.src('src/*.js')
        .pipe(gulp.dest('dist'))
        .pipe(browserSync.stream());
});

gulp.task('images', function() {
    return gulp.src('src/images/**/*') // Получаем все изображения
        .pipe(imagemin()) // Оптимизируем изображения
        .pipe(gulp.dest('dist/images')) // Копируем в dist/images
        .on('end', function() {
            log('Images have been optimized and copied to dist/images'); // Логируем успешное копирование
        })
        .on('error', function(err) {
            log.error('Image copy error:', err); // Логируем ошибки, если они есть
        });
});

gulp.task('serve', function() {
    browserSync.init({
        server: {
            baseDir: 'dist'
        },
        port: 3072, // Указываем порт явно
        ui: false // Отключаем интерфейс BrowserSync
    });

    gulp.watch('src/styles.css', gulp.series('styles'));
    gulp.watch('src/*.html', gulp.series('html'));
    gulp.watch('src/*.js', gulp.series('scripts'));
    gulp.watch('src/images/**/*', gulp.series('images'));
});


gulp.task('default', gulp.series('styles', 'html', 'scripts', 'images', 'serve'));
